def Feb(yy):
    if(yy%4==0):
        if(yy%100==0):
            if(yy%400==0):
                leap = 1
            else:
                leap = 0
        else:
            leap = 1
    else:
        leap = 0

    if(leap==0):
        feb = 28
    else:
        feb = 29
    return feb

def createDir(dir_name):
    try:    
        os.mkdir(dir_name)
        return True;
    except:
        return False

def folderExists(dir_name):
    ct=0
    while(True):
            if(os.path.exists(dir_name)):
                break
            else:
                check = createDir(username)
                if(check):
                    break
                else:
                    if(ct==3):
                        print('Unable to Create Folder')
                        sys.exit()
                    ct+=1

def fileExists(string):
    counter = 0
    while(1):
                try:
                    if(counter == 0):    
                        f = open(os.path.join(username, "{}.txt".format(string)), 'r', encoding = 'utf-8-sig')
                    else:
                        f = open(os.path.join(username, "{} - {}.txt".format(string,counter)), 'r', encoding = 'utf-8-sig')
                    f.close()
                except FileNotFoundError:
                    return counter
                counter+=1

def Pre(A):
    if(A<10):
        A = "0"+str(A)
    else:
        A = str(A)
    return A

def DateTime(date):
    global tzhh
    global tzmm
    yy = int(date[0:4])
    mn = int(date[5:7])
    dd = int(date[8:10])
    hh = int(date[11:13])
    mm = int(date[14:16])
    ss = int(date[17:19])
    day31 = {1,3,5,7,8,10,12}
    
    daytime=((hh*3600)+(mm*60)+ss)
    if(tzhh<0):
        tz = ((tzhh*3600)-(tzmm*60))
    else:
        tz = ((tzhh*3600)+(tzmm*60))
    left = daytime + tz
    if((left%86400)<43200):
        st = 'AM'
    else:
        st = 'PM'

    if(tzhh<0):        
        if(left<0):
            if(dd<=1):
                if(mn<=1):
                    yy -= 1
                    mn = 12
                    dd = 31
                else:
                    mn -= 1
                    if(mn in day31):
                        dd = 31
                    elif(mn==2):
                        dd = Feb(yy)
                    else:
                        dd = 30
            else:
                dd-=1
            left = 86400 + left
            
    elif(tzhh>=0):
        day = int(left/86400)
        left = (left%86400)
        if(day==1):
            if(mn in day31):
                if(dd==31):
                    if(mn == 12):
                        yy += 1
                        mn = 1
                        dd = 1
                    else:       
                        mn+=1
                        dd = 1
                else:
                    dd+=1
            elif(mn==2):
                if(dd==Feb(yy)):
                     dd = 1
                     mn += 1
                else:
                    dd += 1                        
            else:
                if(dd==30):
                    dd = 1
                    mn+=1
                else:
                    dd+=1

    hh = int(left/3600)
    if(st=='PM'):
        hh = hh-12
    if(hh==0):
        hh = 12
        
    mm = int((left%3600)/60)
    ss = int(left%60)
        
    TimeStamp = Pre(hh)+':'+Pre(mm)+':'+Pre(ss)+' '+st+', '+Pre(dd)+'/'+Pre(mn)+'/'+Pre(yy)
    return(TimeStamp)

